/*jshint esversion: 6 */
/**
 * Helper methods to respond with same struct for each requests
 */

module.exports = {
  success: (res, data, setLocal = true) => {
    if (setLocal)
      res.locals = data;

    return res.status(200).json({
      data: data
    });
  },
  withError: (next, error, res) => {
    if (res) {
      res.locals = error;
    }
    return next(error);
  }
};